import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import Form from "react-bootstrap/Form";
import axios from "axios";
import { ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants";

export default class AddUser extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      email: "",
      password: "",
      redirectToDisplayAllUsers:
        localStorage.accessLevel < ACCESS_LEVEL_ADMIN,
      wasSubmittedAtLeastOnce: false,
    };
  }

  componentDidMount() {
    this.inputToFocus.focus();
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
  
    const { name, email, password } = this.state;
  
    if (!name || !email || !password) {
      // If any field is empty, set the error state
      this.setState({ wasSubmittedAtLeastOnce: true });
      return;
    }
  
    const userData = {
      name: name,
      email: email,
      password: password,
    };
  
    axios
      .post(`${SERVER_HOST}/users/register/${name}/${email}/${password}`, userData)
      .then((res) => {
        this.setState({ redirectToDisplayAllUsers: true });
      })
      .catch((err) => {
        console.error("Error adding user:", err);
        this.setState({ wasSubmittedAtLeastOnce: true });
      });
  };

  render() {
    let errorMessage = "";
    if (this.state.wasSubmittedAtLeastOnce) {
      errorMessage = (
        <div className="error">Error: All fields must be filled in</div>
      );
    }

    return (
      <div className="forms">
        {this.state.redirectToDisplayAllUsers ? (
          <Redirect to="/DisplayAllUsers" />
        ) : null}

        {errorMessage}

        <Form onSubmit={this.handleSubmit}>
          <Form.Group controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              ref={(input) => {
                this.inputToFocus = input;
              }}
              type="text"
              name="name"
              value={this.state.name}
              onChange={this.handleChange}
            />
          </Form.Group>

          <Form.Group controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </Form.Group>

          <Form.Group controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              name="password"
              value={this.state.password}
              onChange={this.handleChange}
            />
          </Form.Group>

          <button type="submit" className="green-button">
            Add User
          </button>

          <Link className="red-button" to={"/DisplayAllUsers"}>
            Cancel
          </Link>
        </Form>
      </div>
    );
  }
}
