import React, { Component } from "react";
import CarTableRow from "./CarTableRow";
import FilterComponent from "./FilterComponent";

export default class ShirtGrid extends Component {
  constructor(props) 
  {

    super(props)

    this.state = {
      filteredCars: [] // Initialize with an empty array
    };

    this.handleFilterChange = this.handleFilterChange.bind(this);

  }

  componentDidMount() 
  {

    // Set the initial state using props.cars
    this.setState({ filteredCars: this.props.cars });

  }

  handleFilterChange(filterValue) 
  {
    const filteredCars = this.props.cars.filter(car => car.name.toLowerCase().includes(filterValue.toLowerCase())
    );

    this.setState({ filteredCars });
  }

  render() 
  {
    return (
      
      <div>
        <FilterComponent onFilterChange={this.handleFilterChange} />
        <div className="itemsGrid">
          {/* Render the filteredCars */}
          {this.state.filteredCars.map(car => (
            <CarTableRow key={car._id} car={car} />
          ))}
        </div>
      </div>
    );
  }
}
