import React, { Component } from "react";
import axios from "axios";

class Modal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedPhoto: null,
    };
  }

  componentDidMount() {
    const { car, photoId, carPhotos } = this.props;
    const selectedPhoto = carPhotos.find((photo) => photo._id === photoId);

    axios
      .get(`${SERVER_HOST}/cars/photo/${selectedPhoto.filename}`)
      .then((res) => {
        this.setState({
          selectedPhoto: {
            ...selectedPhoto,
            image: res.data.image,
          },
        });
      })
      .catch((err) => {
        console.error("Error loading photo:", err);
      });
  }

  render() {
    const { isOpen, onClose, car } = this.props;
    const { selectedPhoto } = this.state;

    if (!isOpen || !selectedPhoto) return null;

    return (
      <div className="modal">
        <div className="modal-content">
          <div className="modal-flex">
            <div className="modal-text">
              <span className="close" onClick={onClose}>&times;</span>
              <h1>{car.name}</h1>
              <p>Price: â‚¬{car.price}</p>
              <p>Gender: {car.gender}</p>
              <p>Colour: {car.colour}</p>
              <p>Size: {car.size}</p>
              <p>Fabric: {car.fabric}</p>
              <p>Description: {car.description}</p>
            </div>

            <div className="modal-image">
              <img src={`data:;base64,${selectedPhoto.image}`} alt="" />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Modal;
