import React, { Component } from "react";

class BasketPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      basketItems: JSON.parse(localStorage.getItem("basketItems")) || [],
      initialQuantities: {}, // Store initial quantities of items
    };

    // Bind methods to the class instance
    this.handleDelete = this.handleDelete.bind(this);
    this.handleIncreaseQuantity = this.handleIncreaseQuantity.bind(this);
    this.handleDecreaseQuantity = this.handleDecreaseQuantity.bind(this);
  }

  componentDidMount() {
    // Initialize initialQuantities based on current basketItems
    const initialQuantities = {};
    this.state.basketItems.forEach(item => {
      initialQuantities[item.id] = item.quantity;
    });
    this.setState({ initialQuantities });
  }

  handleDelete(index) {
    const { basketItems } = this.state;
    const itemId = basketItems[index].id;
    const updatedBasketItems = basketItems.filter(item => item.id !== itemId);
  
    localStorage.setItem("basketItems", JSON.stringify(updatedBasketItems));
    this.setState({ basketItems: updatedBasketItems });
  }

  handleIncreaseQuantity(id) {
    const { basketItems } = this.state;
    const updatedBasketItems = basketItems.map(item => {
      if (item.id === id) {
        return { ...item, quantity: item.quantity + 1 };
      }
      return item;
    });
  
    localStorage.setItem("basketItems", JSON.stringify(updatedBasketItems));
    this.setState({ basketItems: updatedBasketItems });
  }
  
  handleDecreaseQuantity(id) {
    const { basketItems } = this.state;
    const updatedBasketItems = basketItems.map(item => {
      if (item.id === id && item.quantity > 0) {
        return { ...item, quantity: item.quantity - 1 };
      }
      return item;
    });
  
    localStorage.setItem("basketItems", JSON.stringify(updatedBasketItems));
    this.setState({ basketItems: updatedBasketItems });
  }

  render() {
    const { basketItems, initialQuantities } = this.state;
    const groupedItems = {};

    // Group items based on their IDs
    basketItems.forEach(item => {
      if (!groupedItems[item.id]) {
        groupedItems[item.id] = { ...item, quantity: initialQuantities[item.id] }; // Initialize quantity to initial quantity
      } else {
        groupedItems[item.id].quantity = initialQuantities[item.id];
      }
    });

    return (
      <div className="basket-page">
        <h1>Basket</h1>
        {Object.values(groupedItems).map((item, index) => (
          <div key={index} className="basket-item">
            <p>Name: {item.name}</p>
            <p>Price: â‚¬{item.price}</p>
            <p>Quantity: {item.quantity}</p>
            <button onClick={this.handleDelete.bind(this, index)}>Delete</button>
            <button onClick={() => this.handleIncreaseQuantity(item.id)}>+</button>
            <button onClick={() => this.handleDecreaseQuantity(item.id)}>-</button>
          </div>
        ))}
      </div>
    );
  }
}

export default BasketPage;
