import React, { Component } from "react";
import CarTableRow from "./CarTableRow";
import SearchFilter from "./SearchFilter";

class ShirtGrid extends Component {
  constructor(props) {
    super(props);

    this.state = {
      filterValue: "",
      sortBy: null,
      sortOrder: "asc", // Default sorting order is ascending
    };

    this.handleFilterChange = this.handleFilterChange.bind(this);
    this.handleSort = this.handleSort.bind(this);
  }

  handleFilterChange(filterValue) {
    this.setState({ filterValue });
  }

  handleSort(event) {
    const sortBy = event.target.value;
    this.setState({ sortBy });
  }

  render() {
    const { filterValue, sortBy, sortOrder } = this.state;
    let { cars } = this.props;

    // Apply filter
    const filteredCars = filterValue
      ? cars.filter((car) =>
          car.name.toLowerCase().includes(filterValue.toLowerCase())
        )
      : cars;

    // Apply sorting
    if (sortBy) {
      cars = [...filteredCars].sort((a, b) => {
        const aValue = a[sortBy];
        const bValue = b[sortBy];
        if (sortOrder === "asc") {
          return aValue.localeCompare(bValue);
        } else {
          return bValue.localeCompare(aValue);
        }
      });
    } else {
      cars = filteredCars;
    }

    return (
      <div>
        <SearchFilter
          onFilterChange={this.handleFilterChange}
          filterValue={filterValue}
        />
        <div>
          <label htmlFor="sortDropdown">Sort by:</label>
          <select id="sortDropdown" onChange={this.handleSort} value={sortBy}>
            <option value="">-- Select --</option>
            <option value="name">Name</option>
            {/* Add additional options for other sortable attributes */}
          </select>
        </div>
        <div className="itemsGrid women_section">
          {cars.map((car) => (
            <CarTableRow key={car._id} car={car} />
          ))}
        </div>
      </div>
    );
  }
}

export default ShirtGrid;

