import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import { SERVER_HOST } from "../config/global_constants";

export default class Profile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true
        }
    }

    componentDidMount() {
        if (!localStorage.token) {
            this.setState({ isLoggedIn: false });
        }
        console.log("Profile Photo:", localStorage.profilePhoto); // Add this line to check the value of localStorage.profilePhoto
        // Set profile photo URL
        const profilePhotoUrl = localStorage.profilePhoto || "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVsHOtBsku-BR1Y2GbrBcLfVaUtO1GzbUbIg&usqp=CAU";
        this.setState({ profilePhotoUrl });
    }

    handleLogout = () => {
        localStorage.clear();
        this.setState({ isLoggedIn: false });
        window.location.reload();
    };

    render() {
        const profilePhotoUrl = localStorage.profilePhoto

        if (!this.state.isLoggedIn) {
            return <Redirect to="/DisplayAllCars" />;
        }

        return (
            <div>
                <br></br>
                <center>
                <img src={`data:image/jpeg;base64,${this.state.profilePhoto}`} alt="Profile Pic" />
                    {/* Display other profile information here */}
                    <br></br>
                    <LinkInClass value="Logout" className="red-button" onClick={this.handleLogout} />
                </center>
                <br></br><br></br>
            </div>
        );
    }
}
