import React, { Component } from "react";

export default class CheckboxFilter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: [] // Initially no sizes are selected
    };
    this.handleCheckboxChange = this.handleCheckboxChange.bind(this);
  }

  handleCheckboxChange(event) {
    const { value, checked } = event.target;
    let updatedSizes = [...this.state.sizes];

    if (checked) {
      updatedSizes.push(value);
    } else {
      updatedSizes = updatedSizes.filter(size => size !== value);
    }

    this.setState({ sizes: updatedSizes });
    this.props.onFilterChange(updatedSizes); // Pass selected sizes to parent component
  }

  render() {
    return (
      <div>
        <label>
          <input
            type="checkbox"
            value="S"
            onChange={this.handleCheckboxChange}
          />
          S
        </label>
        <label>
          <input
            type="checkbox"
            value="M"
            onChange={this.handleCheckboxChange}
          />
          M
        </label>
        <label>
          <input
            type="checkbox"
            value="L"
            onChange={this.handleCheckboxChange}
          />
          L
        </label>
        <label>
          <input
            type="checkbox"
            value="XL"
            onChange={this.handleCheckboxChange}
          />
          XL
        </label>
      </div>
    );
  }
}
