import React, { Component } from "react";

class CarDetailsPage extends Component {
  render() {
    const { car } = this.props.location.state;
    const imageUrl = car.photos.length > 0 ? car.photos[0].url : ""; // Get the image URL or set empty string if not available

    return (
      <div>
        <h1>{car.name}</h1>
        <div>
          <img src={imageUrl} alt="Car" onError={(e) => e.target.src = 'placeholder.jpg'} /> {/* Display a placeholder image if URL is not available or if there's an error */}
        </div>
        <div>
          <p>Price: â‚¬{car.price}</p>
          <p>Description: {car.description}</p>
          {/* Add more details as needed */}
        </div>
      </div>
    );
  }
}

export default CarDetailsPage;
