import React, { Component } from "react"
import { Redirect } from "react-router-dom"
import axios from "axios"

import LinkInClass from "../components/LinkInClass"
import { SERVER_HOST } from "../config/global_constants"


export default class Logout extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true,
            redirect: false
        }
    }


    handleSubmit = (e) => {
        e.preventDefault()

        axios.post(`${SERVER_HOST}/users/logout`)
            .then(res => {
                localStorage.clear()

                this.setState({ isLoggedIn: false, redirect: true })
            })
            .catch(err => {
                // do nothing
            })
    }


    render() {
        return (
            <div>

                {!this.state.isLoggedIn ? <Redirect to="/DisplayAllCars" /> : null}

                <LinkInClass value="Log out" className="red-button" onClick={this.handleSubmit} />
            </div>
        )
    }
}
