import React, { Component } from "react";
import axios from "axios";

import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";


class Modal extends Component {

  componentDidMount() {
    this.props.car.photos.map((photo) => {
      return axios
        .get(`${SERVER_HOST}/cars/photo/${photo.filename}`)
        .then((res) => {
          document.getElementById(
            photo._id
          ).src = `data:;base64,${res.data.image}`;
        })
        .catch((err) => {
          // do nothing
        });
    });
    console.log(this.props.car.photos)
  }

    render() {
        const { isOpen, onClose, car, carPhotos } = this.props;
      
        if (!isOpen) return null;
      
        return (
          <div className="modal">
            <div className="modal-content">
              <div className ="modal-flex">

                <div className="modal-text">
                  <span className="close" onClick={onClose}>&times;</span>
                  <h1>{car.name}</h1>
                  <p>Price: â‚¬{car.price}</p>
                  <p>Gender: {car.gender}</p>
                  <p>Colour: {car.colour}</p>
                  <p>Size: {car.size}</p>
                  <p>Fabric: {car.fabric}</p>
                  <p>Description: {car.description}</p>
                </div>

                <div className="modal-image">
                  {this.props.car.photos}
                </div>

              </div>
            </div>
          </div>
        );
      }
    }

export default Modal;
