import React, { Component, Redirect } from "react";
import { Link } from "react-router-dom";

export default class Profile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true,
            redirectToCars: false // Add a state to manage redirection
        }
    }

    componentDidMount() {
        if (!localStorage.token) {
            this.setState({ isLoggedIn: false });
        }
    }

    handleLogout = () => {
        localStorage.clear();
        this.setState({ isLoggedIn: false, redirectToCars: true }); // Update state to trigger redirection
    };

    render() {
        if (!this.state.isLoggedIn) {
            return <Redirect to="/DisplayAllCars" />;
        }

        const profilePhotoUrl =
            localStorage.profilePhoto ||
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVsHOtBsku-BR1Y2GbrBcLfVaUtO1GzbUbIg&usqp=CAU";

        if (this.state.redirectToCars) {
            return <Redirect to="/DisplayAllCars" />;
        }

        return (
            <div>
                <img src={profilePhotoUrl} alt="Profile" />
                {/* Display other profile information here */}
                <button className="red-button" onClick={this.handleLogout}>Logout</button>
            </div>
        );
    }
}
