import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import { SERVER_HOST } from "../config/global_constants";

export default class Profile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true,
            orderName: localStorage.getItem("orderName") || "",
            orderEmail: localStorage.getItem("orderEmail") || "",
            address: localStorage.getItem("address") || "",
            phone: localStorage.getItem("phone") || ""
        }
    }

    componentDidMount() {
        if (!localStorage.getItem("token")) {
            this.setState({ isLoggedIn: false });
        }

        const profilePhotoUrl = localStorage.getItem("profilePhoto");
        const name = localStorage.getItem("name");
        const orderName = localStorage.getItem("orderName") || "";
        const orderEmail = localStorage.getItem("orderEmail") || "";
        const address = localStorage.getItem("address") || "";
        const phone = localStorage.getItem("phone") || "";

        this.setState({ profilePhotoUrl, name, orderName, orderEmail, address, phone });
    }

handleLogout = () => {
    localStorage.clear();
    this.setState({ isLoggedIn: false });
    window.location.reload();
};

handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
};

handleSubmit = (e) => {
    e.preventDefault();

    // Prepare data to be sent to backend
    const { orderName, orderEmail, address, phone } = this.state;
    const { orderID, carID, price } = this.props.location.state.car; // Assuming you have these values passed from the previous component

    // Make API call to save the details to sales records
    axios.post(`/sales/${orderID}/${carID}/${price}`, {
        orderName,
        orderEmail,
        address,
        phone
    })
    .then(response => {
        // Handle success
        alert("Profile Updated Successfully and Sale recorded!");
    })
    .catch(error => {
        // Handle error
        console.error('Error updating profile and recording sale:', error);
    });

    // Update local storage
    localStorage.setItem("orderName", orderName);
    localStorage.setItem("orderEmail", orderEmail);
    localStorage.setItem("address", address);
    localStorage.setItem("phone", phone);
};

render() {
    const { car } = this.props.location.state;
    const profilePhotoUrl = localStorage.getItem("profilePhoto");

    if (!this.state.isLoggedIn) {
        return <Redirect to="/DisplayAllCars" />;
    }

    return (
        <div>
            <br />
            <center>
                <img
                    src={`data:image/png;base64,${profilePhotoUrl}`}
                    alt="Profile Pic"
                    style={{
                        width: "100px",
                        height: "100px",
                        borderRadius: "50%"
                    }}
                />
                <h2>Welcome, {this.state.name}</h2>
                <h2>Your checkout details</h2>
                <form className="profileform" onSubmit={this.handleSubmit}>
                    <div>
                        <label>Name: </label>
                        <input
                            type="text"
                            name="orderName"
                            value={this.state.orderName}
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <label>Email: </label>
                        <input
                            type="email"
                            name="orderEmail"
                            value={this.state.orderEmail}
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <label>Address: </label>
                        <input
                            type="text"
                            name="address"
                            value={this.state.address}
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <label>Phone: </label>
                        <input
                            type="text"
                            name="phone"
                            value={this.state.phone}
                            onChange={this.handleChange}
                        />
                    </div>
                    <button type="submit">Save</button>
                </form>
                <h2>Order History</h2>
                <p>To be added soon...</p>
                <br />
                <LinkInClass
                    value="Logout"
                    className="red-button"
                    onClick={this.handleLogout}
                />
            </center>
            <br /><br />
        </div>
    );
    }
}
