import React, { Component } from "react";

class BasketPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      basketItems: JSON.parse(localStorage.getItem("basketItems")) || [],
    };
  }

  // Function to remove an item from the basket
  removeItem(index) {
    const updatedBasket = [...this.state.basketItems];
    updatedBasket.splice(index, 1); // Remove the item at the specified index
    this.setState({ basketItems: updatedBasket });
    localStorage.setItem("basketItems", JSON.stringify(updatedBasket)); // Update local storage
  }

  // Function to increase the quantity of an item
  increaseQuantity(index) {
    const updatedBasket = [...this.state.basketItems];
    updatedBasket[index].quantity += 1; // Increase quantity
    this.setState({ basketItems: updatedBasket });
    localStorage.setItem("basketItems", JSON.stringify(updatedBasket)); // Update local storage
  }

  // Function to decrease the quantity of an item
  decreaseQuantity(index) {
    const updatedBasket = [...this.state.basketItems];
    if (updatedBasket[index].quantity > 1) {
      updatedBasket[index].quantity -= 1; // Decrease quantity if it's greater than 1
      this.setState({ basketItems: updatedBasket });
      localStorage.setItem("basketItems", JSON.stringify(updatedBasket)); // Update local storage
    }
  }

  render() {
    const { basketItems } = this.state;
    return (
      <div className="basket-page">
        <h1>Basket</h1>
        <div className="basket-items">
          {basketItems.map((item, index) => (
            <div key={index} className="basket-item">
              <p>Name: {item.name}</p>
              <p>Price: â‚¬{item.price}</p>
              <p>Quantity: {item.quantity}</p>
              <button onClick={() => this.removeItem(index)}>Remove</button>
              <button onClick={() => this.increaseQuantity(index)}>+</button>
              <button onClick={() => this.decreaseQuantity(index)}>-</button>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default BasketPage;
