import React, { Component } from "react";
import axios from "axios";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      address: "",
      formSubmitted: false,
      errorMessage: "",
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, address } = this.state;
    try {
      // Send POST request to backend endpoint with form data
      const response = await axios.post("/api/submitForm", {
        name,
        email,
        address,
      });
      // Handle successful form submission
      this.setState({ formSubmitted: true });
    } catch (error) {
      // Handle errors
      this.setState({ errorMessage: "An error occurred. Please try again." });
    }
  };

  render() {
    const { formSubmitted, errorMessage } = this.state;

    // Render success message if form submitted successfully
    if (formSubmitted) {
      return <p>Form submitted successfully!</p>;
    }

    return (
      <form onSubmit={this.handleSubmit}>
        {errorMessage && <p>{errorMessage}</p>}
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          value={this.state.name}
          onChange={this.handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Your Email"
          value={this.state.email}
          onChange={this.handleChange}
          required
        />
        <textarea
          name="address"
          placeholder="Your Address"
          value={this.state.address}
          onChange={this.handleChange}
          required
        ></textarea>
        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default Dashboard;
