import React, { Component } from "react";
import axios from "axios";
import { ACCESS_LEVEL_GUEST, SERVER_HOST } from "../config/global_constants";
import BuyCar from "./BuyCar";

class CarDetailsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentIndex: 0,
      imageUrls: [],
      basketItems: JSON.parse(localStorage.getItem("basketItems")) || [],
    };
  }

  componentDidMount() {
    const { car } = this.props.location.state;
    const filename = car.photos.length > 0 ? car.photos[0].filename : "";

    axios
      .get(`${SERVER_HOST}/cars/photo/${filename}`)
      .then((res) => {
        const imageUrl = `data:image/jpeg;base64,${res.data.image}`;
        this.setState({ imageUrl });
      })
      .catch((err) => {
        console.error("Error fetching image:", err);
      });

    this.loadImage(this.state.currentIndex);
  }

  // loadImage(index) {
  //   const photo = this.props.location.state.car.photos[index];
  //   if (photo) {
  //     axios
  //       .get(`${SERVER_HOST}/cars/photo/${photo.filename}`)
  //       .then((res) => {
  //         const imageUrl = `data:image/jpeg;base64,${res.data.image}`;
  //         document.getElementById(photo._id).src = imageUrl;
  //       })
  //       .catch((err) => {
  //         // Handle error
  //         console.error("Error fetching image:", err);
  //       });
  //   }
  // }

  loadImage(index) {
    const photo = this.props.location.state.car.photos[index];
    if (photo) {
      axios
        .get(`${SERVER_HOST}/cars/photo/${photo.filename}`)
        .then((res) => {
          document.getElementById(
            photo._id
          ).src = `data:;base64,${res.data.image}`;
        })
        .catch((err) => {
          // Handle error
        });
    }
  }

  handlePrevClick() {
    this.setState(
      (prevState) => ({
        currentIndex: Math.max(prevState.currentIndex - 1, 0),
      }),
      () => this.loadImage(this.state.currentIndex)
    );
  }

  handleNextClick() {
    this.setState(
      (prevState) => ({
        currentIndex: Math.min(
          prevState.currentIndex + 1,
          this.state.imageUrls.length - 1
        ),
      }),
      () => this.loadImage(this.state.currentIndex)
    );
  }

  addToBasket() {
    const { car } = this.props.location.state;
    const imageUrl = this.state.imageUrls[this.state.currentIndex];

    const newItem = {
      id: car._id,
      name: car.name,
      price: car.price,
      imageUrl: imageUrl,
      quantity: 1,
    };

    let basketItems = JSON.parse(localStorage.getItem("basketItems")) || [];
    basketItems.push(newItem);
    localStorage.setItem("basketItems", JSON.stringify(basketItems));
    this.setState({ basketItems: basketItems });
  }

  render() {
    const { car } = this.props.location.state;
    const { currentIndex, imageUrls } = this.state;
    const hasMultipleImages = car.photos.length > 1;

    return (
      <div className="product-details-container">
        <div className="product-image">
          <img src={imageUrls[currentIndex] || "placeholder.jpg"} alt="Car" />
          {hasMultipleImages && (
            <>
              <div className="prev" onClick={() => this.handlePrevClick()}>
                &lt;
              </div>
              <div className="next" onClick={() => this.handleNextClick()}>
                &gt;
              </div>
            </>
          )}
        </div>
        <div className="product-info">
          {/* ... other details ... */}
          <div>
            {localStorage.accessLevel <= ACCESS_LEVEL_GUEST &&
              (this.props.location.state.car.sold !== true ? (
                <React.Fragment>
                  <button
                    className="basket-button"
                    onClick={() => this.addToBasket()}
                  >
                    Add to Basket
                  </button>
                  <BuyCar
                    carID={this.props.location.state.car._id}
                    price={this.props.location.state.car.price}
                  />
                </React.Fragment>
              ) : (
                "SOLD"
              ))}
          </div>
        </div>
      </div>
    );
  }
}

export default CarDetailsPage;
