import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

class UserTableRow extends Component {
  constructor(props) {
    super(props);

    this.state = {
      profilePhotoSrc: null
    };
  }

  componentDidMount() {
    this.loadProfilePhoto();
  }

  loadProfilePhoto() {
    const { profilePhotoFilename } = this.props.user;
    if (profilePhotoFilename) {
      axios
        .get(`${SERVER_HOST}/users/photo/${profilePhotoFilename}`)
        .then((res) => {
          this.setState({
            profilePhotoSrc: `data:;base64,${res.data.image}`
          });
        })
        .catch((err) => {
          // Handle error
        });
    }
  }

  render() {
    const { user } = this.props;
    const { profilePhotoSrc } = this.state;

    return (
      <div>
        <div className="userRow">
          {profilePhotoSrc && (
            <img src={profilePhotoSrc} alt="Profile" />
          )}
          <div className="userInfo">
            <h3>{user.name}</h3>
            <p>Email: {user.email}</p>
            {/* You can display other user information here */}
          </div>
          <p>
            {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
              <Link
                className="green-button"
                to={"/EditUser/" + user._id}
              >
                Edit
              </Link>
            ) : null}

            {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
              <Link
                className="red-button"
                to={"/DeleteUser/" + user._id}
              >
                Delete
              </Link>
            ) : null}
          </p>
        </div>
      </div>
    );
  }
}

export default UserTableRow;
