import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import BuyCar from "./BuyCar";

import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

class CarTableRow extends Component {
  componentDidMount() {
    this.props.car.photos.forEach((photo) => {
      axios
        .get(`${SERVER_HOST}/cars/photo/${photo.filename}`)
        .then((res) => {
          document.getElementById(
            photo._id
          ).src = `data:;base64,${res.data.image}`;
        })
        .catch((err) => {
          // Handle error
        });
    });
  }

  render() {
    const { car, cars } = this.props; // Destructure cars prop
    let soldOrForSale = null;
    if (localStorage.accessLevel <= ACCESS_LEVEL_GUEST) {
      if (car.sold !== true) {
        soldOrForSale = (
          <BuyCar carID={car._id} price={car.price} />
        );
      } else {
        soldOrForSale = "SOLD";
      }
    }

    return (
      <Link to={{ pathname: "/carDetails/" + car._id, state: { cars: cars } }} className="itemsBox"> {/* Pass cars prop */}
        <div className="carPhotos">
          {car.photos.map((photo) => (
            <img key={photo._id} id={photo._id} alt="" />
          ))}
        </div>
        <div className="details">
          <h3>{car.name}</h3>
          <i>â‚¬{car.price}</i>
        </div>
        <p>
          {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
            <Link
              className="green-button"
              to={"/EditCar/" + car._id}
            >
              Edit
            </Link>
          ) : null}

          {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
            <Link
              className="red-button"
              to={"/DeleteCar/" + car._id}
            >
              Delete
            </Link>
          ) : null}

          {soldOrForSale}
        </p>
      </Link>
    );
  }
}

export default CarTableRow;
