import React, { Component } from "react";

class CarDetailsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imageError: false // Initialize imageError state to false
    };
  }

  handleImageError = () => {
    this.setState({ imageError: true }); // Set imageError state to true if there's an error loading the image
  };

  render() {
    const { car } = this.props.location.state;
    let imageUrl = ""; // Initialize imageUrl variable

    // Check if car object and photos array exist
    if (car && car.photos && car.photos.length > 0) {
      imageUrl = car.photos[0].url; // Get the image URL from the car object
    }

    return (
      <div>
        <h1>{car.name}</h1>
        <div>
          {/* Display the image with the provided URL or a placeholder image */}
          <img src={imageUrl} alt="Car" onError={this.handleImageError} />
          {this.state.imageError && <p>Error loading image.</p>} {/* Display error message if there's an error loading the image */}
        </div>
        <div>
          <p>Price: â‚¬{car.price}</p>
          <p>Description: {car.description}</p>
          {/* Add more details as needed */}
        </div>
      </div>
    );
  }
}

export default CarDetailsPage;
