import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import { SERVER_HOST } from "../config/global_constants";

export default class Profile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true,
            orderName: "",
            orderEmail: "",
            address: localStorage.getItem("address") || "",
            phone: localStorage.getItem("phone") || ""
        }
    }

    componentDidMount() {
        if (!localStorage.getItem("token")) {
            this.setState({ isLoggedIn: false });
        } else {
            // Fetch user details from backend
            axios.get(`${SERVER_HOST}/sales/user/details`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`
                }
            })
            .then(response => {
                const { orderName, orderEmail } = response.data;
                this.setState({ orderName, orderEmail });
            })
            .catch(error => {
                console.error("Error fetching user details:", error);
                // Handle error, maybe redirect to login page
            });
        }

        const profilePhotoUrl = localStorage.getItem("profilePhoto");
        const name = localStorage.getItem("name");

        this.setState({ profilePhotoUrl, name });
    }

    // Other methods remain the same...

    render() {
        const profilePhotoUrl = localStorage.getItem("profilePhoto");

        if (!this.state.isLoggedIn) {
            return <Redirect to="/DisplayAllCars" />;
        }

        return (
            <div>
                <br />
                <center>
                    <img
                        src={`data:image/png;base64,${profilePhotoUrl}`}
                        alt="Profile Pic"
                        style={{
                            width: "100px",
                            height: "100px",
                            borderRadius: "50%"
                        }}
                    />
                    <h2>Welcome, {this.state.name}</h2>
                    <h2>Your checkout details</h2>
                    <form className="profileform" onSubmit={this.handleSubmit}>
                        <div>
                            <label>Name: </label>
                            <input
                                type="text"
                                name="orderName"
                                value={this.state.orderName}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <label>Email: </label>
                            <input
                                type="email"
                                name="orderEmail"
                                value={this.state.orderEmail}
                                onChange={this.handleChange}
                            />
                        </div>
                        {/* Address and Phone fields remain unchanged */}
                        <button type="submit">Save</button>
                    </form>
                    {/* Order History and Logout sections remain unchanged */}
                </center>
                <br /><br />
            </div>
        );
    }
}
