import React, { Component } from "react";
import CarTableRow from "./CarTableRow";
import CheckboxFilter from "./CheckboxFilter";

export default class ShirtGrid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      filteredShirts: [] // Initialize with an empty array
    };
    this.handleFilterChange = this.handleFilterChange.bind(this);
  }

  componentDidMount() {
    // Set the initial state using props.cars
    this.setState({ filteredShirts: this.props.cars });
  }

  handleFilterChange(selectedSizes) {
    // Filters the shirts based on selected sizes
    const filteredShirts = this.props.cars.filter(
      shirt => selectedSizes.includes(shirt.size)
    );
    this.setState({ filteredShirts });
  }

  render() {
    return (
      <div>
        <CheckboxFilter onFilterChange={this.handleFilterChange} />
        <div className="itemsGrid">
          {/* Renders the filtered shirts */}
          {this.state.filteredShirts.map(shirt => (
            <CarTableRow key={shirt._id} shirt={shirt} />
          ))}
        </div>
      </div>
    );
  }
}
