import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import { SERVER_HOST } from "../config/global_constants";

export default class Profile extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true,
            name: localStorage.getItem("name") || "",
            email: localStorage.getItem("email") || "",
            address: localStorage.getItem("address") || "",
            phone: localStorage.getItem("phone") || ""
        }
    }

    componentDidMount() {
        if (!localStorage.getItem("token")) {
            this.setState({ isLoggedIn: false });
        }

        const profilePhotoUrl = localStorage.getItem("profilePhoto");
        this.setState({ profilePhotoUrl });
    }

    handleLogout = () => {
        localStorage.clear();
        this.setState({ isLoggedIn: false });
        window.location.reload();
    };

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        // Here you can save the updated details to the server or localStorage
        // For simplicity, I'll just update localStorage
        localStorage.setItem("name", this.state.name);
        localStorage.setItem("email", this.state.email);
        localStorage.setItem("address", this.state.address);
        localStorage.setItem("phone", this.state.phone);
        alert("Profile Updated Successfully!");
    };

    render() {
        const profilePhotoUrl = localStorage.getItem("profilePhoto");

        if (!this.state.isLoggedIn) {
            return <Redirect to="/DisplayAllCars" />;
        }

        return (
            <div>
                <br />
                <center>
                    <img
                        src={`data:image/png;base64,${profilePhotoUrl}`}
                        alt="Profile Pic"
                        style={{
                            width: "100px",
                            height: "100px",
                            borderRadius: "50%"
                        }}
                    />
                    <h2>Welcome, {this.state.name}</h2>
                    <form onSubmit={this.handleSubmit}>
                        <div>
                            <label>Name: </label>
                            <input
                                type="text"
                                name="name"
                                value={this.state.name}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <label>Email: </label>
                            <input
                                type="email"
                                name="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <label>Address: </label>
                            <input
                                type="text"
                                name="address"
                                value={this.state.address}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <label>Phone: </label>
                            <input
                                type="text"
                                name="phone"
                                value={this.state.phone}
                                onChange={this.handleChange}
                            />
                        </div>
                        <button type="submit">Save</button>
                    </form>
                    <h2>Order History</h2>
                    <p>To be added soon...</p>
                    <br />
                    <LinkInClass
                        value="Logout"
                        className="red-button"
                        onClick={this.handleLogout}
                    />
                </center>
                <br /><br />
            </div>
        );
    }
}
