import React, { Component } from "react";
import axios from "axios";

import { SERVER_HOST } from "../config/global_constants";

class CarDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      car: null,
      loading: true,
      error: null,
    };
  }

  componentDidMount() {
    const { carId } = this.props.match.params;
    axios
      .get(`${SERVER_HOST}/cars/${carId}`)
      .then((res) => {
        this.setState({ car: res.data, loading: false });
      })
      .catch((err) => {
        this.setState({ error: "Failed to fetch car details", loading: false });
      });
      
  }

  render() {
    const { car, loading, error } = this.state;

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>{error}</div>;
    }

    return (
      <div className="car-details">
        <h2>{car.name}</h2>
        <div>
          <img src={`${SERVER_HOST}/cars/photo/${car.photos[0].filename}`} alt="" />
        </div>
        <div>
          <p>Price: â‚¬{car.price}</p>
          <p>Description: {car.description}</p>
          <p>Year: {car.year}</p>
          <p>Condition: {car.condition}</p>
          <p>Owner: {car.owner}</p>
          <p>Sold: {car.sold ? "Yes" : "No"}</p>
        </div>
      </div>
    );
  }
}

export default CarDetails;
