import React, { Component } from "react";

class BasketPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      basketItems: JSON.parse(localStorage.getItem("basketItems")) || [],
    };
  }

  handleDelete(index) {
    const basketItems = [...this.state.basketItems];
    basketItems.splice(index, 1);
    localStorage.setItem("basketItems", JSON.stringify(basketItems));
    this.setState({ basketItems: basketItems });
  }

  handleIncreaseQuantity(index) {
    const basketItems = [...this.state.basketItems];
    basketItems[index].quantity++;
    localStorage.setItem("basketItems", JSON.stringify(basketItems));
    this.setState({ basketItems: basketItems });
  }

  handleDecreaseQuantity(index) {
    const basketItems = [...this.state.basketItems];
    if (basketItems[index].quantity > 1) {
      basketItems[index].quantity--;
      localStorage.setItem("basketItems", JSON.stringify(basketItems));
      this.setState({ basketItems: basketItems });
    }
  }

  render() {
    const { basketItems } = this.state;
    const groupedItems = {};

    // Group items based on their names or IDs
    basketItems.forEach(item => {
      if (!groupedItems[item.id]) {
        groupedItems[item.id] = [item];
      } else {
        groupedItems[item.id].push(item);
      }
    });

    return (
      <div className="basket-page">
        <h1>Basket</h1>
        {Object.keys(groupedItems).map(id => (
          <div key={id} className="basket-items-group">
            <h2>{groupedItems[id][0].name}</h2>
            {groupedItems[id].map((item, index) => (
              <div key={index} className="basket-item">
                <p>Name: {item.name}</p>
                <p>Price: â‚¬{item.price}</p>
                <p>Quantity: {item.quantity}</p>
                <button onClick={() => this.handleDelete(index)}>Delete</button>
                <button onClick={() => this.handleIncreaseQuantity(index)}>+</button>
                <button onClick={() => this.handleDecreaseQuantity(index)}>-</button>
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  }
}

export default BasketPage;
