import React, { Component } from "react";
import axios from "axios";
import { ACCESS_LEVEL_GUEST, SERVER_HOST } from "../config/global_constants";
import BuyCar from "./BuyCar";

class CarDetailsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imageUrl: "",
      basketItems: JSON.parse(localStorage.getItem("basketItems")) || [],
      itemAddedToBasket: false, // State to track if item is added to basket
    };
  }

  componentDidMount() {
    const { car } = this.props.location.state;
    const filename = car.photos.length > 0 ? car.photos[0].filename : "";

    axios
      .get(`${SERVER_HOST}/cars/photo/${filename}`)
      .then((res) => {
        const imageUrl = `data:image/jpeg;base64,${res.data.image}`;
        this.setState({ imageUrl });
      })
      .catch((err) => {
        console.error("Error fetching image:", err);
      });
  }

  addToBasket() {
    const { car } = this.props.location.state;
    const filename = car.photos.length > 0 ? car.photos[0].filename : "";
    axios
      .get(`${SERVER_HOST}/cars/photo/${filename}`)
      .then((res) => {
        const imageUrl = `data:image/jpeg;base64,${res.data.image}`;

        const newItem = {
          id: car._id,
          name: car.name,
          price: car.price,
          imageUrl: imageUrl,
          quantity: 1,
        };

        let basketItems = JSON.parse(localStorage.getItem("basketItems")) || [];
        basketItems.push(newItem);
        localStorage.setItem("basketItems", JSON.stringify(basketItems));
        this.setState({ basketItems: basketItems, itemAddedToBasket: true }); // Update state to indicate item is added to basket
      })
      .catch((err) => {
        console.error("Error fetching image:", err);
      });
  }

  render() {
    const { car } = this.props.location.state;
    const { itemAddedToBasket } = this.state;

    return (
      <div className="product-details-container">
        <div className="product-image">
          <img src={this.state.imageUrl || "placeholder.jpg"} alt="Car" />
        </div>
        <div className="product-info">
          <h1 className="product-title">{car.name}</h1>
          <div className="product-price">
            <p>Price: â‚¬{car.price}</p>
          </div>
          <div className="product-fabric">
            <p>Fabric: {car.fabric}</p>
          </div>
          <div className="product-description">
            <p>Description: {car.description}</p>
          </div>
          <div>
            {localStorage.accessLevel <= ACCESS_LEVEL_GUEST &&
            !itemAddedToBasket && // Render button only if item not added to basket
            !this.props.location.state.car.sold ? ( // Check if item is not sold
              <React.Fragment>
                <button className="basket-button" onClick={() => this.addToBasket()}>
                  Add to Basket
                </button>
                <BuyCar
                  carID={this.props.location.state.car._id}
                  price={this.props.location.state.car.price}
                />
              </React.Fragment>
            ) : (
              "SOLD"
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default CarDetailsPage;
