import React, { Component } from "react"
import { withRouter } from "react-router-dom"
import axios from "axios"

import LinkInClass from "../components/LinkInClass"
import { SERVER_HOST } from "../config/global_constants"

class Logout extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoggedIn: true
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()

        axios.post(`${SERVER_HOST}/users/logout`)
            .then(res => {
                localStorage.clear()

                this.setState({ isLoggedIn: false }, () => {
                    // Redirect to DisplayAllCars
                    this.props.history.push("/DisplayAllCars")
                })
            })
            .catch(err => {
                // do nothing
            })
    }

    render() {
        return (
            <div>
                <LinkInClass value="Log out" className="red-button" onClick={this.handleSubmit} />
            </div>
        )
    }
}

// Wrap the component with withRouter to access the history object
export default withRouter(Logout)
