import React from "react"
import ReactDOM from "react-dom"
import "./css/index.css"
import App from "./App"
import * as serviceWorker from "./serviceWorker"

function handleScroll() {
    var element = document.querySelector(".navbar");
    var scroll = window.scrollY || window.pageYOffset;

    if (element) {
        if (scroll > 550) {
            element.style.transition = "background-color 0.6s, color 0.6s"; // Adding transition for smooth color change
            element.style.background = "orange";
            element.style.color = "black"; // Change text color to black when background is orange
        } else {
            element.style.transition = "background-color 0.3s, color 0.3s"; // Adding transition for smooth color change
            element.style.background = "black";
            element.style.color = "orange"; // Change text color back to orange when background is black
        }
    }
}

document.addEventListener('scroll', handleScroll);


ReactDOM.render(<App/>, document.getElementById(`root`))

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister()
